<script>
import scroll from 'smooth-scrollbar'
// import Vue from 'vue'

// import Vue from 'vue'
export default {
  name: 'Index',
  data () {
    return {
      title: '{ Lorem }',
      sub: 'You Innovate, We Scale',
      show: false,
      slider: null
    }
  },

  mounted: function () {
    // update file of #app
    /* var app = new Vue({
        el: '#app'
      })
      app.showed() */
    var vm = this
    // alert(vm.show)
    vm.$parent.triggerIndexState()

    var scrollbarOptions = {
      renderByPixels: true,
      continuousScrolling: false
    }
    var v = 'index'
    var div = document.getElementById(v)
    // console.log(div)
    if (!div) {}
    scroll.init(div, scrollbarOptions)
  },

  beforeMount () {
    // var scrolled = 0
    var vm = this

    window.addEventListener('wheel', function (event) {
      var div = document.getElementById('index')

      if (!div) {
        return
      }
      const scrollbar = scroll.init(div)

      // console.log(scrollbar.offset.y)

      // hide
      // hide
      if (scrollbar.offset.y > 40) {
        vm.$router.push('whoweare')
      }

      if (scrollbar.offset.y > 1) {
        vm.slider = 'leave'
      }
    })
    // console.log(scrolled)
  },
  destroyed () {
    var vm = this
    vm.$parent.defaultState()
  }
}
</script>

